
package insaneColourdeTriangles;

public class InsaneColouredTriangles {
	public static char triangle(String row) {
		String newRow = "";
		String temp;
		int rowLength;

		while (row.length() > 2) {
			rowLength = row.length();

			// Skip every second row by using 3 instead of 2 characters
			for (int i = 0; i < rowLength - 2; i++) {
				temp = row.substring(i, i + 3);

				switch (temp) {
				case "RRR":
				case "RBB":
				case "RGG":
				case "GGR":
				case "GBG":
				case "GRB":
				case "BBR":
				case "BGB":
				case "BRG":
					newRow += "R";
					break;
				case "BBB":
				case "BRR":
				case "RRB":
				case "RBG":
				case "RGR":
				case "GGB":
				case "GBR":
				case "GRG":
				case "BGG":
					newRow += "B";
					break;
				default:
					newRow += "G";
				}
			}
//			System.out.println();
//			System.out.println(row);
			row = newRow;
			newRow = "";
		}

		// Get the final character
		switch (row) {
		case "GG":
		case "BR":
		case "RB":
			return 'G';
		case "RR":
		case "BG":
		case "GB":
			return 'R';
		case "BB":
		case "GR":
		case "RG":
			return 'B';
		default:
			// Only one or none character in row
			return row.length() > 0 ? row.charAt(0) : ' ';
		}
	}

	public static void main(String[] args) {
		final String normal = "";
		final String reverse = new StringBuilder(normal).reverse().toString();
		final long start = System.currentTimeMillis();
		System.out.println(triangle(normal));
		System.out.println(triangle(reverse));

		final long finishTime = System.currentTimeMillis();
		System.out.println("That took: " + (finishTime - start) + " ms");
	}
}
